-- ----------------------------
-- Table structure for sys_log
-- ----------------------------
DROP TABLE IF EXISTS `sys_log`;
CREATE TABLE `sys_log` (
  `id` bigint(20) NOT NULL COMMENT 'ID',
  `title` varchar(255) DEFAULT '' COMMENT '日志标题',
  `service_id` varchar(32) DEFAULT NULL COMMENT '微服务ID',
  `user_id` bigint(20) DEFAULT NULL COMMENT '操作用户id',
  `username` varchar(64) DEFAULT NULL COMMENT '操作用户名',
  `client_ip` varchar(255) DEFAULT NULL COMMENT '客户端IP',
  `user_agent` varchar(1000) DEFAULT NULL COMMENT '用户代理',
  `url` varchar(255) DEFAULT NULL COMMENT '请求URL',
  `uri` varchar(255) DEFAULT NULL COMMENT '请求URI',
  `method` varchar(10) DEFAULT NULL COMMENT '请求方式 GET POST DELETE PUT等',
  `params` varchar(1000) DEFAULT NULL COMMENT '操作提交请求参数',
  `time` bigint(20) DEFAULT NULL COMMENT '请求执行时长 单位 ms',
  `exception_message` varchar(255) DEFAULT NULL COMMENT '异常信息',
  `type` tinyint(1) NOT NULL DEFAULT '1' COMMENT '日志类型 1 正常日志 2 错误日志',
  `create_time` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  `update_time` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '更新时间',
  `create_user_id` bigint(20) DEFAULT NULL COMMENT '创建人',
  `update_user_id` bigint(20) DEFAULT NULL COMMENT '更新人',
  `is_deleted` bigint(20) DEFAULT '0' COMMENT '逻辑删除',
  PRIMARY KEY (`id`),
  KEY `idx_username` (`username`),
  KEY `idx_type` (`type`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_bin COMMENT='系统操作日志';

