-- ----------------------------
-- Table structure for user
-- ----------------------------
DROP TABLE IF EXISTS `user`;
CREATE TABLE `user` (
  `id` bigint(20) NOT NULL COMMENT 'ID',
  `username` varchar(100) NOT NULL COMMENT '用户名',
  `password` varchar(100) NOT NULL COMMENT '密码',
  `name` varchar(64) DEFAULT NULL COMMENT '用户姓名',
  `mobile` char(11) DEFAULT NULL COMMENT '手机号',
  `avatar` varchar(255) DEFAULT NULL COMMENT '头像',
  `gender` int(11) NOT NULL DEFAULT '0' COMMENT '性别 0 未知 1 男 2 女',
  `city` varchar(32) DEFAULT NULL COMMENT '城市',
  `signature` varchar(100) DEFAULT NULL COMMENT '签名',
  `description` varchar(500) DEFAULT NULL COMMENT '简介',
  `type` int(10) NOT NULL DEFAULT '1' COMMENT '类型 1 普通用户 2 管理员 3 官方号 4 马甲号',
  `enabled` tinyint(1) NOT NULL DEFAULT '1' COMMENT '是否有效用户 0 否 1 是',
  `account_non_expired` tinyint(1) NOT NULL DEFAULT '1' COMMENT '账号是否未过期 0 否 1 是',
  `credentials_non_expired` tinyint(1) NOT NULL DEFAULT '1' COMMENT '密码是否未过期 0 否 1 是',
  `account_non_locked` tinyint(1) NOT NULL DEFAULT '1' COMMENT '是否未锁定 0 否 1 是',
  `status` tinyint(1) NOT NULL DEFAULT '1' COMMENT '状态 0 未激活 1 激活',
  `create_time` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  `update_time` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '更新时间',
  `create_user_id` bigint(20) DEFAULT NULL COMMENT '创建人',
  `update_user_id` bigint(20) DEFAULT NULL COMMENT '更新人',
  `is_deleted` bigint(20) DEFAULT '0' COMMENT '逻辑删除',
  PRIMARY KEY (`id`),
  UNIQUE KEY `uk_user_username` (`username`),
  UNIQUE KEY `uk_user_mobile` (`mobile`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_bin COMMENT='用户';

